import React from 'react';
import './CleanPlaylist.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class CleanPlaylist extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      userId: this.props.user,
      ownerId: '',
      playlistName: this.props.name,
      explicitTracks: [],
      cleanTracks: [],
      NewPlaylistTracks: [],
    };
    this.makeClean = this.makeClean.bind(this)
  }


  componentDidMount(){
    console.log("mounted")
    var playlistId = this.props.data
    var ownerId = this.props.id
    this.getTracksData(ownerId, playlistId)
  }

  getTracksData(owner, id){
    var explicitTracksList = []
    var cleanTracksList = []
    spotifyApi.getPlaylistTracks(owner, id)
      .then((response) => {
        console.log(response)

        response.items.map((item) =>{
          if(item.track.explicit === true){
            explicitTracksList.push(item.track)
          }else{
            cleanTracksList.push(item.track)
          }
        })
          this.setState({
            explicitTracks: explicitTracksList,
            cleanTracks: cleanTracksList
          })
          console.log(this.state.explicitTracks)
          console.log(this.state.cleanTracks)
      })
    }

    findCleanTracks(){
      const explicitList = this.state.explicitTracks;
      explicitList.map((itemE) =>{
        console.log(itemE)
        spotifyApi.search('track:' + '"' +itemE.name+ '"'  + ' artist:'+ '"' +itemE.artists[0].name + '"',['track'])
          .then((response) => {
            console.log(response)
            response.tracks.items.map((itemC) =>{
              console.log(itemC)
              if (itemE.artists[0].name === itemC.artists[0].name && itemE.name === itemC.name && !itemC.explicit || itemC.name.includes("Clean")){
                this.setState((state) =>{
                  const cleanTracks = state.cleanTracks.concat(itemC)
                  return{
                    cleanTracks: cleanTracks
                  };
                });
                console.log("YES: " + itemC.name)
              }else{
                console.log("couldn't add: " + itemC.name)
              }
            })
          })
      })
    }

    makeClean = () => {
      // this.findCleanTracks();
      console.log(this.state.cleanTracks)
      spotifyApi.createPlaylist(this.state.userId, {name: this.state.playlistName + "(Clean)"})
        .then((response) =>{
          console.log(response)
        })
    }

  render(){
    this.findCleanTracks();
    const test = this.state.cleanTracks
    console.log(test)


    return(
      <div>
      <button type="button" className="btn btn-lg btn-success" onClick= {this.makeClean}>Clean Playlist</button>
      </div>

    )
  }
}

export default CleanPlaylist
