import React from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: []
    };

  }

  getPlaylistData(){
    var playlistItems= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
             response.items.map((index) => {
               playlistItems.push({name:index.name, image: index.images[0], url: index.href})
             })
            this.setState({
              playlistList: playlistItems
            })
         });
         console.log(playlistItems)
   }

   componentDidMount(){
     this.getPlaylistData()
   }


  render(){


    const showPlaylist =
      this.state.playlistList.map(function(name , image, url){
        return <div key={name}> <h1>{name}</h1> </div>
      });

    return (
      <div>
        {showPlaylist}
      </div>
    );

  }
}



export default Playlists
