          return(
            <div key= {index}>
              <h1> {list.name} </h1>
              <img src= {list.images[0].url} style ={{width: 100}} />
            </div>
          )
        })
      });
  }

  render(){
    return (
      <div className="App">
        <img src={logo} />
        <h1> Cleanify </h1>

        <a href='http://localhost:8888'>
        <button type="button" className="btn btn-light btn-lg"> Login With Spotify </button>
        </a>

          { this.state.loggedIn &&
              this.getPlaylists()
          }

      </div>
    );
  }
}

export default App;
