import React from 'react';
import './CleanPlaylist.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class CleanPlaylist extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      explicitTracks: [],
      cleanTracks: [],
      NewPlaylistTracks: []
    };
    this.createNewClean = this.createNewClean.bind(this)
    this.ownerId = this.ownerId.bind(this)
    console.log(ownerId)
  }

  componentDidMount(){
    const playlistId = this.props.data
    const ownerId = this.props.id
    this.getTracksData(ownerId, playlistId)
  }

  getTracksData(owner, id){
    var explicitTracksList = []
    var cleanTracksList = []
    spotifyApi.getPlaylistTracks(owner, id)
      .then((response) => {
        console.log(response)

        response.items.map((item) =>{
          if(item.track.explicit === true){
            explicitTracksList.push(item)
          }else{
            cleanTracksList.push(item)
          }
        })
          this.setState({
            explicitTracks: explicitTracksList,
            cleanTracks: cleanTracksList
          })
          console.log(this.state.explicitTracks)
          console.log(this.state.cleanTracks)

      })
    }

    createNewClean(){
      var explicitList = this.state.explicitTracks
      var cleanList = []
      explicitList.map((itemE) =>{
        // console.log(itemE)
        spotifyApi.search('track:' + '"' +itemE.track.name+ '"'  + ' artist:'+ '"' +itemE.track.artists[0].name + '"',['track'])
          .then((response) => {
            console.log(response)
            response.tracks.items.map((itemC) =>{
              // console.log(itemC)
              if (itemE.track.artists[0].name === itemC.artists[0].name && itemE.track.name === itemC.name && !itemC.explicit || itemC.name.includes("Clean")){
                cleanList.push(itemC)
                console.log("YES" + itemC.name)
              }else{
                console.log("couldn't add " + itemC.name)
              }
            })
          })
      })
      this.setState({
        cleanTracks: this.state.cleanTracks.push(cleanList)
      })
      spotifyApi.createPlaylist()
        .then((response) => {
          console.log(response)
        })
      console.log(this.state.cleanTracks)
    }

  render(){
    return(
      <div>
      <button type="button" className="btn btn-lg btn-success" onClick={this.createNewClean}>Clean Playlist</button>
      </div>
    )
  }
}

export default CleanPlaylist
