       console.log(x.uri)
     }


      // spotifyApi.createPlaylist(this.state.userId, {name: this.state.playlistName + "(Clean)"})
        // .then((response) =>{
        //   console.log(response)
        // })
    }

  render(){
    const trackUri = this.cleanTracks
    console.log(trackUri)


    return(
      <div>
      <button type="button" className="btn btn-lg btn-success" onClick= {() => this.makeCleanPlaylist(trackUri)}>Clean Playlist</button>
      </div>

    )
  }
}

export default CleanPlaylist
