import React from 'react';
import './Playlists.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: [],
      playlistSelected: false,
      currentSelected: ''
    };
    const userId= ''
    spotifyApi.getMe()
    .then((response) => {
      userId = response.id
    })

  }

  getPlaylistData(){
    var playlistItems= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
           console.log(response)

             response.items.map((item) => {
               playlistItems.push({id: item.id, name:item.name, image: item.images[0].url})
               return playlistItems
             })
            this.setState({
              playlistList: playlistItems
            })
            console.log(this.state.playlistList)
         });

   }

   componentDidMount(){
     this.getPlaylistData()
   }

   playlistIsSelected = (event) => {

     var currentPlaylist = event.target.getAttribute("id")
     this.setState({
       playlistSelected: true,
       currentSelected: currentPlaylist
     });
     spotifyApi.getPlaylistTracks("1218999994",currentPlaylist)
      .then((response) => {
        console.log(response)
      })

   }


  render(){
    const selected = this.playlistIsSelected

    const showPlaylist =
      this.state.playlistList.map(function(playlist){
        return (
          <div key={playlist.id} className="col-md-4 col-lg-3 col-6 card">
              <img src={playlist.image} id= {playlist.id} className="card-img card-img-top" onClick= {selected} alt="Card image cap"/>
              <div className= "card-button">
                <button type="button" id= {playlist.id} className="btn" onClick= {selected}> <i className="fas fa-play fa-3x text-white"></i></button>
              </div>
             <div className="card-body">
               <p className="card-text font-weight-bold">{playlist.name}</p>
             </div>
          </div>
           )
      });

    return (
      <div className="Playlist">

      {
        this.state.playlistSelected ?
        <iframe src= {"https://open.spotify.com/embed/playlist/" + this.state.currentSelected} width="100%" height="500" frameBorder="0" allowtransparency="true" allow="encrypted-media"></iframe>
        : <div className="user-playlists">
            <h3 className="font-weight-bold">Playlists</h3>
            <hr className="divider mb-5"/>
            <div className="row">
              {showPlaylist}
              </div>
          </div>
      }
      </div>




    );

  }
}


export default Playlists
