import React from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: []
    };

  }

  getPlaylistData(){
    var playlistItems= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
           console.log(response)

             response.items.map((item) => {
               playlistItems.push({id: item.id, name:item.name, image: item.images[0].url, url: item.href})
             })
            this.setState({
              playlistList: playlistItems
            })
         });
         console.log(playlistItems)
   }

   componentDidMount(){
     this.getPlaylistData()
   }


  render(){


    const showPlaylist =
      this.state.playlistList.map(function(playlist){
        return
          <div class="col-md-4" key={playlist.id}>
               <div class="card mb-4 box-shadow">
                 <img class="card-img-top" src= {playlist.image} alt="Card image cap"/>
                 <div class="card-body">
                   <p class="card-text">{playlist.name}</p>
                   <div class="d-flex justify-content-between align-items-center">
                     <div class="btn-group">
                       <button type="button" class="btn btn-sm btn-outline-secondary">View</button>
                       <button type="button" class="btn btn-sm btn-outline-secondary">Edit</button>
                     </div>
                     <small class="text-muted">9 mins</small>
                   </div>
                 </div>
               </div>
             </div>
        //
        // <div key={playlist.id}>
        //         <img src= {playlist.image} width="60"/>
        //         <h1>{playlist.name}</h1>
        //        </div>
      });

    return (
      <div>
        {showPlaylist}
      </div>
    );

  }
}



export default Playlists
