import React from 'react';
import './Playlists.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: []
    };

  }

  getPlaylistData(){
    var playlistItems= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
           console.log(response)

             response.items.map((item) => {
               playlistItems.push({id: item.id, name:item.name, image: item.images[0].url, url: item.href})
             })
            this.setState({
              playlistList: playlistItems
            })
         });
         console.log(playlistItems)
   }

   componentDidMount(){
     this.getPlaylistData()
   }


  render(){


    const showPlaylist =
      this.state.playlistList.map(function(playlist){
        return (
          <div key={playlist.id} className="col-md-4 col-lg-3 col-5" >
               <div className="card mb-4 box-shadow">
                 <div className="card-img">
                 <img src={playlist.image} className="card-img-top" alt="Card image cap"/>
                 </div>
                 <div className="card-body">
                   <p className="card-text">{playlist.name}</p>
                   <div className="d-flex justify-content-between align-items-center">
                     <div className="btn-group">
                       <button type="button" className="btn btn-sm btn-outline-secondary">View</button>
                     </div>
                   </div>
                 </div>
               </div>
             </div>
           )

      });

    return (
      <div className="row">
        {showPlaylist}
      </div>
    );

  }
}



export default Playlists
