import React from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: []
    };

  }

  getPlaylistData(){
    var playlistNames= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
             console.log(response);
             response.items.map((index) => {
               playlistNames.push(index.name)
             })

         });
    return (
      this.setState({
        playlistList: playlistNames
      })
    )
   }


  render(){

    getPlaylistData()

    const showPlaylist =
      this.state.playlistList.map(function(name){
        return <div key={name}> <h1>{name}</h1> </div>
      });

    return (
      <div>
        {showPlaylist}
      </div>
    );

  }
}



export default Playlists
