import React from 'react';
import './Playlists.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class Playlists extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      playlistList: [],
      playlistSelected: false
    };

  }

  getPlaylistData(){
    var playlistItems= [];

      spotifyApi.getUserPlaylists()
         .then((response, onRejected) => {
           console.log(response)

             response.items.map((item) => {
               playlistItems.push({id: item.id, name:item.name, image: item.images[0].url, url: item.external_urls.spotify})
             })
            this.setState({
              playlistList: playlistItems
            })
         });
   }

   componentDidMount(){
     this.getPlaylistData()
   }

   playlistIsSelected(){
     this.setState({
       playlistSelected: true
     });
     console.log("YUP")
   }


  render(){
    const playlistIsSelected = this.playlistIsSelected


    const showPlaylist =
      this.state.playlistList.map(function(playlist){
        return (
          <div key={playlist.id} className="col-md-4 col-lg-3 col-6 card">
              <img src={playlist.image} className="card-img-top" onClick= {() => this.playlistIsSelected} alt="Card image cap"/>
             <div className="card-body">
               <p className="card-text">{playlist.name}</p>
             </div>
          </div>
           )

      });

    return (
      <div className="row">
        {showPlaylist}
      </div>
    );

  }
}


export default Playlists
