import React, {Component} from 'react';
import logo from './logo.png';
import './App.css';
import Playlists from './components/Playlists'


import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class App extends Component {

constructor (){
  super();
  const params = this.getHashParams();
  const token = params.access_token;

  if (token) {
     spotifyApi.setAccessToken(token);
  }

  this.state ={
    userId: '',
    loggedIn: params.access_token ? true : false,
    profPic: '',
    name: ''
  }
}
 getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getUserProfile(){
    spotifyApi.getMe()
      .then((response) => {
        console.log(response)
        this.setState ({
          userId: response.id,
          profPic: response.images[0].url,
          name: response.display_name
        })
      })
  }

  componentDidMount(){
    if(this.state.loggedIn){
      this.getUserProfile()
    }
    console.log(this.state.userId)
  }



  render(){
    return (

      <div className="App">

        {
          this.state.loggedIn
          ? <div>
              <div className="row profile">
                <img src= {this.state.profPic} className="profPic rounded-circle" alt="profile pic"/>
                <h1 className="profName">{this.state.name}</h1>
              </div>
              <Playlists userId= {this.state.userId}></Playlists>
            </div>
          : <div>
              <img src={logo} alt="logo"/>
                <h1> Cleanify </h1>
                <a href='http://localhost:8888'>
                  <button type="button" className="btn btn-success btn-lg"> Login With Spotify </button>
                </a>
            </div>
        }


      </div>
    );
  }
}

export default App;
