import React from 'react';
import './CleanPlaylist.css';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

class CleanPlaylist extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      userId: this.props.user,
      playlistName: this.props.name,
      playlistId: this.props.data,
      ownerId: '',
      buttonPressed: false,
      newPlaylistId: '',
      unavalible: false,
    };
    this.explicitTracks = [];
    this.cleanTracks = [];
    this.noCleanVersions = [];
    this.recTracks = [];
    this.makeCleanPlaylist = this.makeCleanPlaylist.bind(this)
    this.findCleanTracks = this.findCleanTracks.bind(this)
    this.getRecommended = this.getRecommended.bind(this)
  }

  getTracksData(owner, id){
    var explicitTracksList = []
    var cleanTracksList = []
    spotifyApi.getPlaylistTracks(owner, id)
      .then((response) => {
        console.log(response)

        response.items.map((item) =>{
          if(item.track.explicit === true){
            explicitTracksList.push(item.track)
          }else{
            cleanTracksList.push(item.track)
          }
        })
      })
      this.explicitTracks = explicitTracksList
      this.cleanTracks = cleanTracksList
      console.log(this.explicitTracks)
      console.log(this.cleanTracks)

    }

    componentDidMount(){
      var playlistId = this.props.data
      var ownerId = this.props.id
      this.getTracksData(ownerId, playlistId)
    }

    findCleanTracks(name, artist){
        return spotifyApi.search('track:' + '"' +name+ '"'  + ' artist:'+ '"' +artist+ '"',['track'])
    }

    async makeCleanPlaylist(){
      var itemE;
      for (itemE of this.explicitTracks) {
        let searchResult = await this.findCleanTracks(itemE.name, itemE.artists[0].name)
        console.log(searchResult)
        var itemC;
        var counter = 0;
        for (itemC of searchResult.tracks.items){
          console.log(itemC)
          if (itemE.artists[0].name === itemC.artists[0].name && itemE.name === itemC.name && !itemC.explicit || itemC.name.includes("Clean")){
            if (counter < 1){
              this.cleanTracks.push(itemC)
              console.log(" ADDED: " + itemC.name)
            }
            counter ++
          }else{
            console.log("couldn't add: " + itemC.name)
          }
        }
        if (counter === 0 ){
          this.noCleanVersions.push(itemE)
          this.setState({
            unavalible:true
          })
        }
      }
      this.getRecommended(this.noCleanVersions)

      console.log(this.cleanTracks)
      console.log(this.noCleanVersions)

       var trackUri = [];
       var newId = '';
       var x;
       for (x of this.cleanTracks){
         trackUri.push(x.uri)
       }
        let playlistResult = await spotifyApi.createPlaylist(this.state.userId, {name: this.state.playlistName + "(Clean)"})
        console.log(playlistResult)
        newId = playlistResult.id

        let addResult = await spotifyApi.addTracksToPlaylist(this.state.userId, newId, trackUri)
        console.log(addResult)

        this.setState({
          newPlaylistId: newId,
          buttonPressed: true
        })

    }

    async getRecommended(tracks){
      var trackId = [];
      tracks.map((item) => {
        trackId.push(item.id)
      })
      let recommededResult = await spotifyApi.getRecommendations({seed_tracks: trackId})
      console.log(recommededResult)

      recommededResult.tracks.map((item) => {
        if (!item.explicit){
          this.recTracks.push(item)
        }
      })
      console.log(this.recTracks)
    }

  render(){

    const unavalible =
    this.noCleanVersions.map(function(item) {
      return (
        <div>
        <p key={item.id}> {item.name}</p>
        </div>
      )
    });

    const recommendedTracks =
    this.recTracks.map(function(item) {
      return (
        <div>
        <hr className="col-divider"/>
        <div className="row">
            <div className="col">
              <p key={item.id}> Add</p>
            </div>

            <div className="col-6 text-left">
              <p key={item.id}> {item.name}</p>
            </div>

            <div className="col text-left">
              <p key={item.id}> {item.artists[0].name}</p>
            </div>
        </div>
        <hr/>
        </div>
      )
    });



    return(
      <div className= "Clean">

      {
        this.state.buttonPressed ?
        <div className= "mx-auto">
          <h3 className="font-weight-bold text-success"> Your Playlist Is Cleanified! </h3>
          <p> You're done! We have already saved it to your library!</p>
          <div className="row">
            <div className="col">
              <iframe src= {"https://open.spotify.com/embed/playlist/" + this.state.playlistId} width="80%" height="500" frameBorder="0" allowtransparency="true" allow="encrypted-media" title="embeded-playlist"></iframe>
            </div>
            <i className="fas fa-arrow-right text-success fa-2x"></i>
            <div className="col">
              <iframe src= {"https://open.spotify.com/embed/playlist/" + this.state.newPlaylistId} width="80%" height="500" frameBorder="0" allowtransparency="true" allow="encrypted-media" title="embeded-playlist"></iframe>
            </div>

          </div>
          <hr className="divider mb-5"/>

          <div>
          <h2>Recommended Songs</h2>
          <p>Based on the songs we were unable to add: </p>
          {recommendedTracks}
          </div>

        </div>
        :
          <div>
          <button type="button" className="btn btn-lg btn-success" onClick= {this.makeCleanPlaylist}>Clean Playlist</button>
          <div>
            <iframe src= {"https://open.spotify.com/embed/playlist/" + this.state.playlistId} width="80%" height="500" frameBorder="0" allowtransparency="true" allow="encrypted-media" title="embeded-playlist"></iframe>
          </div>

          </div>
      }

      </div>
    )
  }
}

export default CleanPlaylist
